async def custom_Temperature(self, ctx, *args):
    user = ctx.author.mention
    if len(args) > 0 and args[0].lower() == "concise":
        await ctx.send(f"Nice to meet you {user}! How can I help you?")
    elif len(args) > 0 and args[0].lower() == "creative":
        await ctx.send(f"Hey {user}, let's get this party started! What would you like to do?")
    else:
        await ctx.send(f"Hello {user}! How can I help you today?")
